func SumGps(grid [][]string) int {
	sum := 0
	for i := 0; i < len(grid); i++ {
		for j := 0; j < len(grid[i]); j++ {
			if grid[i][j] == "O" {
				sum += 100*i + j
			}
		}
	}
	return sum
}

type Pair struct {
	X, Y int
}

func CalculateEndGrid(grid [][]string, instructions string) [][]string {
	x, y := InitRobotPosition(grid)
	directions := map[uint8][2]int{
		'^': {-1, 0},
		'>': {0, 1},
		'<': {0, -1},
		'v': {1, 0},
	}
	for i := 0; i < len(instructions); i++ {
		newX := x + directions[instructions[i]][0]
		newY := y + directions[instructions[i]][1]
		if grid[newX][newY] == "#" {
			continue
		} else if grid[newX][newY] == "." {
			x, y = newX, newY
		} else if grid[newX][newY] == "O" {
			for grid[newX][newY] == "O" {
				newX = newX + directions[instructions[i]][0]
				newY = newY + directions[instructions[i]][1]
			}
			if grid[newX][newY] == "#" {
				continue
			} else if grid[newX][newY] == "." {
				pairA := Pair{newX, newY}
				pairB := Pair{x, y}
				for pairA != pairB {
					grid[newX][newY] = grid[newX-directions[instructions[i]][0]][newY-directions[instructions[i]][1]]
					newX = newX - directions[instructions[i]][0]
					newY = newY - directions[instructions[i]][1]
					pairA = Pair{newX, newY}
					pairB = Pair{x, y}
				}
				x = x + directions[instructions[i]][0]
				y = y + directions[instructions[i]][1]
			}
		}
	}

	return grid
}

func InitRobotPosition(grid [][]string) (int, int) {
	for i := 0; i < len(grid); i++ {
		for j := 0; j < len(grid[i]); j++ {
			if grid[i][j] == "@" {
				return i, j
			}
		}
	}

	panic("Nope")
}

func ExtractGrid(file *os.File) [][]string {
	var grid [][]string
	scanner := bufio.NewScanner(file)
	for scanner.Scan() {
		line := scanner.Text()
		grid = append(grid, strings.Split(line, ""))
	}

	return grid
}

func OpenFile(filename string) *os.File {
	pwd, _ := os.Getwd()
	file, err := os.Open(pwd + filename)
	if err != nil {
		log.Fatal(err)
	}

	return file
}
